import { CSSTransition, TransitionGroup } from 'react-transition-group';
import styled from 'styled-components';

const Dot = styled.div`
   background: var(--red);
   color: white;
   border-radius: 50%;
   padding: 0.5rem;
   line-height: 2rem;
   min-width: 3rem;
   margin-left: 1rem;
   font-feature-settings: 'tnum';
   font-variant-numeric: tabular-nums;
`;

const AnimationStyles = styled.span`
   position: relative;
   .count {
      display: block;
      position: relative;
      transition: transform 0.4s;
      backface-visibility: hidden;
   }
   .count-enter {
      background: green;
   }
   .count-enter-active {
      background: yellow;
   }
   .count-exit {
      background: blue;
   }
   .count-exit-active {
      background: pink;
   }
`;

export default function CartCount({ count }) {
   return (
      <AnimationStyles>
         <TransitionGroup>
            <CSSTransition
               unmountOnExit
               className="count"
               classNames="count"
               key={count}
               timeout={{ enter: 400, exit: 300 }}>
               <Dot>{count}</Dot>
            </CSSTransition>
         </TransitionGroup>
      </AnimationStyles>
   );
}
